import React, { Component } from "react";
import { Text, TouchableOpacity, View, ScrollView , TextInput,StyleSheet} from "react-native";
 
import Modal from 'modal-enhanced-react-native-web';
import { Card, ListItem, Button } from 'react-native-elements';
export default class Example extends Component {
  
 

  _values = (props) => (
    state = {
        visibleModal: props.val
      }
  );


  _renderButton = (text, onPress) => (
    <TouchableOpacity onPress={onPress}>
      <View>
        <Text>{text}</Text>
      </View>
    </TouchableOpacity>
  );
 
  _renderModalContent = () => (
    <View style={{flex: 1, justifyContent: 'center'}}>
     <Card >
  <Card.Title>Add User</Card.Title>
  <Card.Divider/>
 <View style={{flexDirection: 'row'}}>
    <Text style={{marginTop:20, alignItems: 'center', textAlign:'center'}}>
     User 
    </Text>
    <TextInput style={styles.input}

placeholder='e.g HG45'
 onChangeText={(val) => setid(val)}
/>


</View>
<View style={{ flexDirection: 'row'}}>
<Text style={{marginTop:20, alignItems: 'center', textAlign:'center'}}>
      Mail
    </Text>
<TextInput style={styles.input}


onChangeText={(val) => setpwd(val)}
/>

</View>
<View style={{ flexDirection: 'row'}}>
<Text style={{marginTop:20, alignItems: 'center', textAlign:'center'}}>
      Pass
    </Text>
<TextInput style={styles.input}

placeholder='*****'
onChangeText={(val) => setpwd(val)}
/>

</View>


    <Button
    
      buttonStyle={{borderRadius: 0, marginLeft: 0, marginRight: 0, marginBottom: 0,marginTop:20}}
      title='Add'  />
  
</Card>
      {this._renderButton("Close", () => this.setState({ visibleModal: false }))}
    </View>
  );
 
  _handleOnScroll = event => {
    this.setState({
      scrollOffset: event.nativeEvent.contentOffset.y
    });
  };
 
  _handleScrollTo = p => {
    if (this.scrollViewRef) {
      this.scrollViewRef.scrollTo(p);
    }
  };
 
  render() {
    return (
      <View>
        {this._renderButton("Modal that can be closed on backdrop press", () =>
          this.setState({ visibleModal: true })
        )}
        <Modal
        
          isVisible={this.state.visibleModal}
          onBackdropPress={() => this.setState({ visibleModal: false })}
        >
          {this._renderModalContent()}
        </Modal>
      </View>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
   
   
  },
  
  b: {
    flex: 1,
   
    alignItems: 'center',
   
   marginTop:50,
  },
  input:{
borderWidth:1,
borderColor: '#777',
padding:8,
margin:10,
width:200,
borderRadius:10,

  }
});
