import { StylesProvider } from '@material-ui/core';
import React, { useState } from 'react';
import {StyleSheet, Text, View, ActivityIndicator, ScrollView} from 'react-native';




 export default function Add (props) {

    const [isLoading, setLoad] = useState(true);
    const [dataSource, setData] = useState(null);
    const mail = props.mail;
    React.useEffect(() => {
        if (props.uid == '' || props.pwd === ''|| props.mail === '') {
          alert("wrong details")
          return;
        }
        else {
         
           fetch('http://localhost:3000/api/user/register/${props.uid}${props.mail}${props.pwd}',{
            method: 'POST',
            headers: {
              'Accept': 'application/json',
            'Content-Type': 'application/json',
            } })
          .then(results => results.json())
          .then(data => {
            setData(data);
          });
    }}, [props.uid,props.pwd,props.mail]); 
    
    
      return (
        <div>
          
          {/* <pre>{JSON.stringify(dataSource)}</pre> */}
        </div>
      )};



   








const styles = StyleSheet.create({
    container: {
        flex:1,
